import React,{useEffect,useState} from 'react'
import TopIcons from '../Icons/TopIcons'

export const Bar = () => {
  
  const [time, setTime] = useState(new Date().toUTCString());

  useEffect(() => {
    const interval = setInterval(() => setTime(Date.now()), 1000);
    return () => {
      clearInterval(interval);
    };
  }, []);

  // var now = time;
  // var now_utc = new Date(now.toUTCString().slice(0, -4));
  // console.log(now_utc);
  
  return (
    <div
      style={{
        zIndex: 6,
        position: "absolute",
        background: "rgba(255, 255, 255, 1)",
        position: "fixed",
        top: "0%",
        width: "100%",
        //left: "50%",
        transform: "translate(0%,0%)",
        // backgroundImage:
        //   "url(" +
        //   "https://images.pexels.com/photos/573130/pexels-photo-573130.jpeg?auto=compress&cs=tinysrgb&dpr=1&w=500" +
        //   ")",
        backgroundRepeat: "repeat",
     
      }}
    >
      <TopIcons />

<div style={{marginTop:"-1.25%",marginLeft:"90%"}}>
{(time*1000).toLocaleString()}
</div>
      
    </div>
  );
}
